import styled from "styled-components";

export const MainButton = styled.div`
  display: flex;
  width: 350px;
  background-color: #363535;
  padding: 10px 25px;
  box-sizing: border-box;
  border-radius: 7px;
  margin-bottom: 5px;
  box-shaadow: 0px 1px 5px 0px #000000;
`;

export const TextButton = styled(MainButton)`
  padding: 18px 28px;
  width: 300px;
`;

export const HomePageStile = styled.div`
  height: 100%;
  padding: 100px 0;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

export const GuildMenuItemStyle = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 8px 8px;
  padding-right: 20px;
  background-color: #363535;
  border-radius: 7px;
  border: 1px solid #3d3d3d;

  margin-bottom: 5px;
`;

export const Container = styled.div`
  width: 1200px;
  margin: 0 auto;

`;

export const AppBarStyle = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 25px;
  box-sizing: border-box;
  background-color: #363535;
  border-radius: 10px;
`;

export const Title = styled.p`
font-size: 22px;
`

type FlexProps = Partial<{
  allignItems: string;
  justifyContent: string;
  flexDirection: string;
  marginBottom: string;
}>
export const Flex = styled.div<FlexProps>`
display: flex;
align-items: ${({allignItems }) => allignItems};
justify-content: ${({justifyContent }) => justifyContent};
flex-direction: ${({flexDirection }) => flexDirection};
margin-bottom: ${({marginBottom }) => marginBottom};
`

export const Grid = styled.div`
display: grid;
grid-template-columns 1fr 1fr 1fr;
`
export const InputField = styled.input`
padding: 8px;
font-size: 14px;
background-color: #363535;
box-sizing: border-box;
color: white;
margin-top: 15px;
border: 1px solid #787878;

border-radius: 7px;
  
`
