import { useContext } from "react";
import { GuildContext } from "../utils/contexts/GuildContext";
import { useWelcomePage } from "../utils/hooks/useWelcomePge";
import { Button, Container, InputField, Page, SelectBar, TextArea, Title } from "../utils/styles";


export const WelcomeMessagePage = () =>
{

  const { guild } = useContext(GuildContext);
  const guildId = (guild && guild.id) || '';
const {config, channels, loading } = useWelcomePage(guildId);
console.log(config);
console.log(channels + "АААААААААААААААААААААААААААААААААААААААААААААААААААААААААААААААААА дщвдвддвв");
  return(



  <Page>
  <Container>
  <Title>Update FCFS Message</Title>
  <div>
  <section> <div>
  <label>Current Channel</label>
  </div>
  <SelectBar>
  <option disabled>Please Select a Channel</option>
  <option>123</option>
  <option>124</option>
  <option>125</option>
  <option>126</option>
  {
    channels?.map((channel => <option selected={channel.id === config?.welcomeChannelId}
      value={channel.id}
      ></option>))
  }
  </SelectBar></section>
  <section>
  <div>
  <label htmlFor="message">Current Message</label>
  </div>
  <TextArea id="message">
  </TextArea>
  <div>
  <Button variant="primary" style={{marginTop:"20px"}}> Save</Button>
  <Button variant="secondary"> Reset</Button></div>
  </section>

  </div>

  </Container>
  </Page>




  )
}
