import { useContext } from "react";
import { MoonLoader } from "react-spinners";
import { updateWelcomeChannelId } from "../utils/api";
import { GuildContext } from "../utils/contexts/GuildContext";
import { useWelcomePage } from "../utils/hooks/useWelcomePge";
import { Button, Container, Flex, InputField, Page, SelectBar, TextArea, Title } from "../utils/styles";


export const WelcomeMessagePage = () =>
{

  const { guild } = useContext(GuildContext);
  const guildId = (guild && guild.id) || '';
const {config, channels, selectedChannel, setSelectedChannel, loading } = useWelcomePage(guildId);

const updateWelcomeChannel = async () => {
  console.log(selectedChannel);
  updateWelcomeChannelId(guildId, selectedChannel || '');
};
  return(



  <Page>
  <Container>
  <Title>Update FCFS Message</Title>
  {channels && config && !loading ?
    <div>
    <section> <div>
    <label>Current Channel</label>
    </div>
    <SelectBar  onChange={(e) =>{
      console.log(e.target.value)
      setSelectedChannel(e.target.value);
    } } >
<option aria-readonly={true} selected={true} disabled={true}>Select a channel</option>

    {channels.map((channel) =>(
       <option
        selected={channel.id === config.welcomeChannelId}
      value={channel.id}
      >
      #{channel.name}
      </option>

    ))}
    </SelectBar>
    </section>
    <section>
    <div>
    <label htmlFor="message">Current Message</label>
    </div>
    <TextArea id="message">
    </TextArea>
    <div>
    <Button variant="primary" onClick={updateWelcomeChannel} style={{marginTop:"20px"}}> Save</Button>
    <Button variant="secondary"> Reset</Button></div>
    </section>

    </div> : <Flex justifyContent='center' allignItems='center'>
    <MoonLoader size={30} color="white"/>
    </Flex>
  }

  </Container>
  </Page>




  )
}
